for i in `shuf iraqi.ssh.servers`; do echo $i|tee -a scan.results.txt;masscan --rate 6000 --ports 0-65535 $i|tee -a scan.results.txt;nmap -v -T4 -sT -n -O -A $i|tee -a scan.results.txt; amap -bq $i 21|grep Proto|tee -a scan.results.txt; amap -bq $i 22|grep Proto|tee -a scan.results.txt; amap -bq $i 80|grep Proto|tee -a scan.results.txt; amap -bq $i 25|grep Proto|tee -a scan.results.txt; amap -bq $i 23|grep Proto|tee -a scan.results.txt;amap -bq $i 53|grep Proto|tee -a scan.results.txt;amap -bq $i 443|grep Proto|tee -a scan.results.txt;  done
